import { Component } from 'react'
import group from '../img/MyGroup.jpg'

import axios from 'axios'
import '../CSS/BlogPage.css'

export default class Blog extends Component {
	render() {
		const getGroupSubscribersCount = async (groupId, accessToken) => {
			try {
				// Запрос на получение информации о группе
				const groupInfoResponse = await axios.get(
					`https://api.vk.com/method/groups.getById?group_id=${groupId}&access_token=${accessToken}&v=5.131`
				)

				// Запрос на получение количества подписчиков группы
				const groupMembersResponse = await axios.get(
					`https://api.vk.com/method/groups.getMembers?group_id=${groupId}&access_token=${accessToken}&v=5.131&count=0`
				)

				// Обработка ответов и получение количества подписчиков
				const subscribersCount = groupMembersResponse.data.response.count

				// Возвращаем количество подписчиков
				return subscribersCount
			} catch (error) {
				// Обработка ошибок
				console.error('Ошибка при получении количества подписчиков:', error)
				return 0
			}
		}
		const groupId = '224123910'
		const accessToken =
			'37b2790237b2790237b27902a234a55e81337b237b279025272b8b1ae7c3d85b80a8310'
		getGroupSubscribersCount(groupId, accessToken)

		getGroupSubscribersCount(groupId, accessToken)
			.then(subscribersCount => {
				console.log('Количество подписчиков:', subscribersCount)
				// Дальнейшая обработка количества подписчиков
			})
			.catch(error => {
				console.error('Ошибка:', error)
			})
		return (
			<main>
				<article className='Post'>
					<header className='Post__header'>
						<img src={group} className='Post_header_img' />
						<div className='Post__header_name_date'>
							<p className='Post__header_nameGroup'>Мой путь в IT</p>
							<time className='Post__date' dateTime='2024-02-03T22:45'>
								2024-02-03 22:45
							</time>
						</div>
					</header>
					<div className='Post__message'>
						Извиняюсь за столь долгое отсутствие. Исходный код с последней
						работы пропал по техническим причинам, поэтому я перестал делать тот
						сайт. Но я начал делать сайт, который я буду сам разрабатывать с
						полного нуля(без дизайна и без готовых работ). Надеюсь вам
						понравится 😁
						<a
							className='Post__link'
							href='https://github.com/WE1337834/MyPortfolioSite'
						>
							Код
						</a>
						<a className='Post__link' href='https://t.me/path_WE1337'>
							Telegram-канал
						</a>
						<a className='Post__link' href='https://vk.com/path_we1337'>
							VK-группа
						</a>
						<img
							className='Post__message_img'
							src='https://sun3-24.userapi.com/impg/l-8tYXUMLcOduNFoKPnU9-b7RECYquoyKiXp4A/dTo_h3G8m5A.jpg?size=1866x922&quality=96&sign=ff13ec6112f8e48102eccce8259264cd&type=album'
						/>
					</div>
				</article>
			</main>
		)
	}
}
