import React from 'react'
import ReactDOM from 'react-dom'
import App from './App' 
import { createRoot } from 'react-dom';


document.addEventListener('DOMContentLoaded', () => {
	const root = document.getElementById('root')
	const select = document.querySelector('.Header__lang')
	const allLang = ['en', 'ru']

	if (select) {
		select.addEventListener('change', changeURLLanguage)
	}

	function changeURLLanguage() {
		let lang = select.value
		window.location.href = window.location.pathname + '#' + lang
		window.location.reload()
	}

	function changeLanguage() {
		let hash = window.location.hash
		hash = hash.substring(1)
		if (!allLang.includes(hash)) {
			window.location.href = window.location.pathname + '#ru'
			window.location.reload()
		}
	}

	changeLanguage() // Call the function to check the initial language on page load

	createRoot(root).render(<App />)
})
