import React, { useEffect } from 'react'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'

import logo from '../img/logo.png'
import Blog from '../pages/BlogPage'
import News from '../pages/News'
import About from '../pages/About'
import Contacts from '../pages/Contacts'
import lang from '../lang'
import '../CSS/Header.css'

function Header() {
  const selectRef = useRef(null); // Use a ref to access the select element

  useEffect(() => {
    const select = selectRef.current; // Get the select element from the ref
    const allLang = ['en', 'ru'];

    select.addEventListener('change', changeURLLanguage);

    function changeURLLanguage() {
      let lang = select.value;
      window.location.href = window.location.pathname + '#' + lang;
      window.location.reload();
      changeLanguage();
    }

    function changeLanguage() {
      let hash = window.location.hash;
      hash = hash.substring(1);
      console.log(hash);
      if (!allLang.includes(hash)) {
        window.location.href = window.location.pathname + '#ru';
        window.location.reload();
      }

      select.value = hash;

      for (let key in lang) {
        const element = document.querySelector('.lng-' + key);
        if (element) {
          element.innerHTML = lang[key][hash];
        }
      }
    }

    changeLanguage();
  }, []);

  return (
    <>
      <header className='Header'>
        <a href='/'>
          <img src={logo} alt='Главная' className='Header__logo' />
        </a>

        <nav className='Header__nav'>
          <a href='/' className='Header__link lng-home'>
            Главная
          </a>
          <a href='/blog' className='Header__link lng-blog'>
            Блог
          </a>
          <a href='/about' className='Header__link lng-about'>
            Обо мне
          </a>
          <a href='/contacts' className='Header__link lng-contacts'>
            Контакты
          </a>
        </nav>
        <select ref={selectRef} className='Header__lang' defaultValue='ru'>
          <option value='ru'>RU</option>
          <option value='en'>EN</option>
        </select>
      </header>
      <Router>
        <Routes>
          <Route exact path='/' element={<News />} />
          <Route exact path='/blog' element={<Blog />} />
          <Route exact path='/about' element={<About selectedLanguage={selectRef.current?.value} />} />
          <Route exact path='/contacts' element={<Contacts />} />
        </Routes>
      </Router>
    </>
  );
}

export default Header
